#define t iTime/3.
#define too_small 1e-6

void mainImage0( out vec4 O, vec2 u )
{
    vec2  R =  iResolution.xy, m=iMouse.xy, z,
          U = ( u+u - R )/ R.y * 1.5,
          M = iMouse.z>0.
          ? vec2( 1. - iMouse.x / R.x * 3.2, 0) 
          : vec2(  1. - mod(iTime/10.,4.), 0);

    float i = 0.;

    z=U;  //Julia set: input is each pixel coordinate

    for ( O *= 0. ; i++ < 13. && dot(z,z) < 1e5 ; ) {

        float modz2 = z.x*z.x*z.x*z.x 
                    + z.y*z.y*z.y*z.y 
                    + 2.*z.x*z.x*z.y*z.y;

        modz2 =  max(modz2,too_small);

        //we are looking for: 1/z^2 + c for which we need to 
        //multiply top and bottom by complex conjugate (!)
        //so we get z!/(z^2*z!^2)+c
        z = vec2(z.x*z.x-z.y*z.y,-2.*z.x*z.y)/modz2
            + M;

    }

    vec2 cc = cos( log( z*z ) *.15 );
    cc *= cc;

    O = vec4(cc,0,0);


}

//FabriceNeyret2 antialiasing trick
void mainImage(out vec4 O, vec2 U) {
    mainImage0(O,U);
    if ( fwidth(length(O)) > .01 ) {  // difference threshold between neighbor pixels
        vec4 o;
        for (int k=0; k < 9; k+= k==3?2:1 )
          { mainImage0(o,U+vec2(k%3-1,k/3-1)/3.); O += o; }
        O /= 9.;
        //O.b++;                        // uncomment to see where the oversampling occurs
    }
}
