float smax(float a, float b, float k)
{
    return log(exp(a*k) + exp(b*k))/k; 
}

float seg(vec2 a, vec2 b, vec2 p)
{
    b-=a; p-=a;
    return length(clamp(dot(p, b)/dot(b,b), 0. , 1.) * b - p);
}

float halfdisc(vec2 c, vec2 dir, float r, vec2 p)
{
    float sdf = length(c-p)-r;
    dir = normalize(dir);
    float proj = dot(p-c, dir);
    if(length(p - (c + proj*dir)) <= r+0.1) sdf = max(sdf, proj);
    return sdf;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv    = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 mouse = (2.*iMouse.xy-iResolution.xy)/iResolution.y;

    float ps = 2. / iResolution.y;

    vec2 refl_offset = mat2(1,1,-1,1)*sqrt(1./2.)*(uv-vec2(0.85,-.25))*vec2(1.8,.8);
    float wave_offset = texture(iChannel2, fragCoord.xy/512.).r;
    float waves = (sin(3.14159265*(wave_offset + uv.y*20.) + 0.8*cos(iTime+(uv.x-1.)*20. * .33 / (+.25-uv.y)))*.125+.5);
    vec3 color = mix(
        mix(

            vec3(0.4, .3,0.1) * (1. + exp(-3. * dot(refl_offset,refl_offset)) )+ exp(-3. * dot(refl_offset,refl_offset))*.1, 
            vec3(0.05, 0.4, 0.8) + 
                (exp(-3. * dot(refl_offset,refl_offset)) + 0.25*exp(-.3 * dot(refl_offset,refl_offset))) * 
                mix(pow(waves, 3.), 1., 0.35)
                ,
            smoothstep(-0.75, -0.7, uv.y+cos(1.5*uv.x-.75)*.05+cos(10.5*uv.x-.75)*.005)
            ),
        vec3(0.1, 0.3, 0.7), 
        smoothstep(-0.025, 0.025, uv.y)
        );

    // Sky sun-scattering
    color += 0.5*exp(-3. * dot(uv-vec2(1), uv-vec2(1)));

    // Clouds, lol
    vec2 cloud_uv = vec2(-.5, -.25)+.4*uv*vec2(1.2,3) + .025*vec2(0, sin(iTime*.4+uv.x*5.) + .25*cos(.1+iTime+uv.x*20.));
    color = mix(color, vec3(1.), 0.13*smoothstep(0.1, -0.1, halfdisc(vec2(-1,.3), vec2(0,-1), 0.35, cloud_uv)));
    color = mix(color, vec3(1.), 0.17*smoothstep(0.1, -0.1, halfdisc(vec2(-1.2,.5), vec2(0.2,-1), 0.35, cloud_uv)));
    color = mix(color, vec3(1.), 0.15*smoothstep(0.1, -0.1, halfdisc(vec2(-.8,.4), vec2(0.3,-1), 0.35, cloud_uv)));

    // parasol
    vec2 parasol_pos = vec2(-1.5, -.25);
    vec2 parasol_dir = vec2(1,-2);
    float parasol_pattern = smoothstep(0.15, 0.15, sin(20. * dot(uv-parasol_pos, parasol_dir)));
    color = mix(color, vec3(0.5)*max(dot(uv-parasol_pos, parasol_dir.yx*vec2(-1,1))+.5, 0.), smoothstep(ps, -ps, seg(parasol_pos, parasol_pos+parasol_dir*.365, uv)-0.03));
    color = mix(color,
        mix(vec3(0.85), vec3(0.95,0.25,0.35), parasol_pattern)
        *max(dot(uv-parasol_pos, parasol_dir.yx*vec2(-1,1))+.85, 0.)
    , smoothstep(ps, -ps, halfdisc(parasol_pos, vec2(1,-2), 0.6, uv)));

    vec4 pos_vel = texelFetch(iChannel0, ivec2(0), 0);

    vec2 pos = pos_vel.xy;
    vec2 vel = pos_vel.zw;

    float rot = texelFetch(iChannel0, ivec2(0), 0).r;


    vec2 peg_pos = get_peg_pos(mouse, iMouse.xy, iTime);

    vec3 ball_color = vec3(1);
    vec2 ball_offset = uv-pos;

    float C = cos(rot), S = sin(rot);
    mat2 R = mat2(C, S, -S, C);
    vec2 spoke_dir = R * ball_offset;

    float spokes = 1.-(smoothstep(0.1, 0.2, cos(10. * atan(spoke_dir.y, spoke_dir.x)))*.75+.25);
    ball_color = mix(ball_color, vec3(0,0,1), spokes);

    float ball_sdf = length(uv-pos)-radius;
    float peg_sdf = length(uv-peg_pos)-peg_radius;

    float pre_dent_sdf = ball_sdf;

    ball_sdf = smax(ball_sdf, -peg_sdf, 10.);

    float r_scaling = max(1.+ pre_dent_sdf - ball_sdf, 0.000001);
    float scaled_radius = radius * r_scaling;

    float ball_offset_len = length(ball_offset);

    vec3 ball_normal = normalize(vec3(ball_offset/r_scaling, sqrt(max(scaled_radius*scaled_radius - dot(ball_offset, ball_offset), 0.))));

    vec3 L = normalize(vec3((vec2(1,1)-pos),0));
    float NdotL = max(dot(ball_normal, vec3(0,1,1)+L), 0.);
    float rim_light = smoothstep(0.3, 1., length(uv - pos - 0.2*normalize(pos-vec2(1)))/(.95+radius));
    color = mix(color, mix(NdotL*.25+.35, 1., 0.1)*ball_color+rim_light, smoothstep(ps, -ps, ball_sdf));

    color = mix(color, vec3(0.5)*(1.-.5*smoothstep(-.1, 0.04, peg_sdf-0.075)), .75*smoothstep(ps, -ps, peg_sdf-0.075));
    color = mix(color, vec3(0), .85*smoothstep(ps, -ps, peg_sdf+0.04));
    color = mix(color, vec3(1), .85*smoothstep(ps, -ps, peg_sdf+0.05));

    float sun_block = smoothstep(0., .35, length(pos-vec2(1))-radius);
    color += 1.5*exp(-3. * dot(uv-vec2(1), uv-vec2(1)) / (.125+sun_block))*sun_block;

    color *= 1.-length(uv)*.1;

    color = tanh(color);
    fragColor = vec4(pow(color, vec3(1./2.2)) + texture(iChannel1, fragCoord.xy/1024.).rgb*(1./256.), 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(ivec2(fragCoord).y != 0 || ivec2(fragCoord).x > 1) return;

    vec2 pos = ball_init_pos;
    vec2 vel = vec2(0);

    float rotation = 0.;

    if(iFrame == 0)
    {
        fragColor = vec4(pos, vel);
        return;
    }

    vec2 mouse = (2. * iMouse.xy - iResolution.xy)/iResolution.y;

    vec4 bufA = texelFetch(iChannel0, ivec2(fragCoord), 0);

    pos = bufA.xy;
    vel = bufA.zw;

    vec2 peg_pos = get_peg_pos(mouse, iMouse.xy, iTime);

    vec2 normal = normalize(pos-peg_pos);

    float proj_vel = dot(vel, normal);
    vec2 rej_vel  = vel - dot(vel, normal) * normal;

    float penetration = length(pos-peg_pos) - radius - peg_radius;
    if(penetration < 0.)
    {
        pos += (pos-peg_pos) / length(pos - peg_pos) * (-penetration);
        vel = max(proj_vel, 0.) * normal + rej_vel;
    }

    vec2 force = 0.075 * smoothstep(radius+peg_radius + 0.0001, radius+peg_radius, length(pos-peg_pos)) * ((pos-peg_pos) / length(pos-peg_pos));

    float gravity = 0.0025;

    force.y -= gravity;

    vec2 accel = force;

    vel += accel;
    pos += 0.5*vel;

    penetration = length(pos-peg_pos) - radius - peg_radius;
    if(penetration <= 0.)
    {
        pos += (pos-peg_pos) / length(pos - peg_pos) * (-penetration);
        vel = max(proj_vel, 0.) * normal + rej_vel;
    }

    if(pos.y < -10.) { pos = ball_init_pos; vel = vec2(0); }

    if(ivec2(fragCoord).x == 0)
        fragColor = vec4(pos, vel);
    else
        fragColor = vec4(rotation, 0, 0, 0);
}

float radius = 0.5;
float peg_radius = 0.1;

vec2 default_peg_pos = vec2(0, -0.5);
vec2 ball_init_pos = vec2(0.045, 1.5);


vec2 get_peg_pos(vec2 mouse_uv, vec2 mouse_xy, float time)
{
    vec2 peg_pos = default_peg_pos;
    peg_pos.x += sin(time*.5)*.5;
    peg_pos.y += cos(time*2.)*.25;
    peg_pos = length(mouse_xy) > 10. ? mouse_uv : peg_pos;
    return peg_pos;
}
