// Hyperspace Flux Shader
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Time variable
    float time = iTime * 0.2;

    // Parameters for hyperspace effect
    float speed = 2.0;
    float layers = 8.0;
    float intensity = 0.5;

    // Initialize color
    vec3 color = vec3(0.0);

    // Loop through layers to create depth
    for (float i = 1.0; i <= layers; i += 1.0)
    {
        // Calculate displacement for current layer
        float layerTime = time * (speed + i * 0.5);
        vec2 disp = uv * (i * intensity + sin(layerTime + i) * 0.5);

        // Calculate angle and radius for polar coordinates
        float angle = atan(disp.y, disp.x);
        float radius = length(disp);

        // Create patterns using sine functions
        float pattern = sin(radius * 10.0 - layerTime * 5.0 + i * 2.0);
        pattern += sin(angle * 5.0 + layerTime * 3.0 + i * 1.5);
        pattern *= 0.5;

        // Compute color for the layer
        vec3 layerColor = vec3(
            sin(pattern + i * 0.3),
            sin(pattern + i * 0.6),
            sin(pattern + i * 0.9)
        );

        // Blend the layer color based on its depth
        color += layerColor / (i * 1.5);
    }

    // Final color adjustments
    color = pow(color, vec3(0.8)); // Gamma correction
    color = clamp(color, 0.0, 1.0); // Ensure color stays within valid range

    fragColor = vec4(color, 1.0);
}
