void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalize coordinates
    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    // Background sky gradient (evening colors)
    vec3 midSkyColor = vec3(0.2, 0.3, 0.6); // Deep blue transitioning upward
    vec3 upperSkyColor = vec3(0.02, 0.03, 0.1); // Dark blue at the top

    vec3 skyColor = mix(midSkyColor, upperSkyColor, smoothstep(0.0, 1.0, uv.y));

    // Subtle noise for texture
    float noise = fract(sin(dot(uv * 500.0, vec2(12.9898, 78.233))) * 43758.5453);
    skyColor += (noise - 0.5) * 0.01; // Add faint noise to sky

    // Light glow near horizon for photorealism
    float glow = exp(-pow(uv.y * 4.0, 2.0)) * 0.1;
    skyColor += vec3(0.2, 0.3, 0.6) * glow; // Soft glow blending into the sky

    // Add sparse random stars to the sky
    float starDensity = 150.0; // Reduce the number of stars
    float randomSeed = fract(sin(dot(uv * starDensity, vec2(12.9898, 78.233))) * 43758.5453);
    float starThreshold = 0.998; // Adjust threshold to make stars rarer
    float stars = step(starThreshold, randomSeed) * 0.4; // Tiny stars with reduced brightness

    // Combine stars into sky color
    skyColor += vec3(stars);

    // Add the moon in the top-right corner
    vec2 moonPos = vec2(0.75, 0.7); // Adjusted moon position for visibility
    float moonSize = 0.12;          // Moon radius
    float moonDist = length(uv - moonPos);
    float moon = smoothstep(moonSize, moonSize - 0.01, moonDist); // Soft moon edge

    // Add glow around the moon
    float moonGlow = exp(-moonDist * 30.0) * 0.5;

    // Combine moon and glow
    vec3 moonColor = vec3(1.0, 1.0, 0.9) * moon;
    vec3 glowColor = vec3(1.0, 1.0, 0.8) * moonGlow;

    skyColor += glowColor; // Add moon glow
    skyColor = mix(skyColor, moonColor, moon); // Add moon surface on top of the sky

    // Moving single cloud with spawn loop
    float speed = 0.02; // Slower cloud movement
    float timeShift = mod(iTime * speed, 2.0); // Loop clouds endlessly

    // Define cloud position moving left to right and reappearing
    vec2 cloudPos = vec2(1.0 - timeShift, 0.6); // Cloud moves from right to left
    float cloudSize = 0.05;                    // Cloud radius
    float cloud = smoothstep(0.0, 0.02, cloudSize - length(uv - cloudPos));

    // Cloud color and blending
    vec3 cloudColor = vec3(0.8, 0.8, 0.85) * cloud;

    // Render single cloud in front of everything
    skyColor = mix(skyColor, cloudColor, cloud * 0.7);

    // Water reflection
    if (uv.y < 0.5) {
        vec2 reflectedUV = uv;
        reflectedUV.y = 1.0 - uv.y; // Flip vertically

        // Simulate ripples using sine waves
        float ripple = sin(reflectedUV.x * 100.0 + iTime * 2.0) * 0.005;
        reflectedUV.x += ripple;
        reflectedUV.y += ripple;

        // Reflected moon
        float reflectedMoonDist = length(reflectedUV - moonPos);
        float reflectedMoon = smoothstep(moonSize, moonSize - 0.01, reflectedMoonDist);
        float reflectedMoonGlow = exp(-reflectedMoonDist * 30.0) * 0.3;
        vec3 reflectedMoonColor = vec3(1.0, 1.0, 0.9) * reflectedMoon;
        vec3 reflectedGlowColor = vec3(1.0, 1.0, 0.8) * reflectedMoonGlow;

        // Reflected clouds
        float reflectedCloud = smoothstep(0.0, 0.02, cloudSize - length(reflectedUV - cloudPos));
        vec3 reflectedCloudColor = vec3(0.7, 0.7, 0.75) * reflectedCloud;

        // Combine reflection
        vec3 reflection = reflectedMoonColor + reflectedGlowColor;
        reflection = mix(reflection, reflectedCloudColor, reflectedCloud * 0.7);

        // Darken water slightly
        skyColor = mix(skyColor, reflection, 0.5);
    }

    // Output the final pixel color
    fragColor = vec4(skyColor, 1.0);
}
