void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

#define IP 32
#define SG 48.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texelFetch(iChannel0, ivec2(fragCoord), 0 ).rgb;
    float xp = (mod(float(iTime*48.), SG))/SG;
    for(int i = 0; i<IP; i++){
        xp+=(1./(SG*float(IP)));
        float yp = 0.5 + (sin(xp*30.))/max(3.,(800.*(pow(abs(0.5-xp), 2.))));
        col *= vec3(0.98,0.995,0.99);
        float dist = distance(uv, vec2(xp,yp));
        col+=vec3(1./(dist*4096.));
    }
    fragColor = vec4(col,1.0);
}

vec2 mainSound( int samp, float time )
{
    float a = sin(6.2831*440.0*mod(time+0.5, 1.))/8.;
    float b = sin(6.2831*996.75*mod(time+0.6, 1.))*exp(-2.5*(mod(time+0.5, 1.)))/2.;
    float c = sin(6.2831*996.75*mod(time+0.8, 1.))*exp(-2.5*(mod(time+0.7, 1.)))/2.;
    return vec2(  a+b+c  );
}
