// ShaderToy Fragment Shader Example
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Преобразование координат в диапазон [0, 1]
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0; // Центрируем координаты в диапазон [-1, 1]

    // Радиус от центра экрана
    float radius = length(uv);
    float angle = atan(uv.y, uv.x);

    // Создаем геометрический узор
    float pattern = sin(10.0 * radius - iTime * 5.0) + cos(10.0 * angle);
    vec3 color = vec3(0.5 + 0.5 * sin(pattern), 0.5 + 0.5 * cos(pattern), 0.5);

    // Устанавливаем цвет пикселя
    fragColor = vec4(color, 1.0);
}
