void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec3 uv = vec3(fragCoord/iResolution.xy, 1);




    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    mat3 rotation = mat3(cos(iTime), sin(iTime), 0, 
                         -sin(iTime), cos(iTime), 0,
                         0, 0, 1);
    mat3 Translation = mat3( 1., 0., 0,
                         0., 1., 0,
                         0.5, 0.5, 1);

     mat3 negTranslation = mat3( 1., 0., 0,
                         0., 1., 0,
                         -0.5, -0.5, 1);

     mat3 rotMatrix = Translation * rotation * negTranslation;
     uv = rotMatrix * uv;

    float line1 = (uv.y+0.5*uv.x-0.5) * 50.;
    line1 = -line1;
    line1 = clamp(line1,0.,1.);
    float line2 = (uv.y - 0.5*uv.x - 0.5) * 50.;
    line2 = clamp(line2,0.,1.);
    float line3 = (uv.y + 0.5*uv.x -1.0) * 50.;
    line3 = clamp(line3,0.,1.);
    float line4 = (uv.y - 0.5*uv.x) * 50.;
    line4 = -line4;
    line4 = clamp(line4, 0.,1.);



    col += line1;
    col += line2;
    col += line3;
    col += line4;




    // Output to screen
    fragColor = vec4(col,0.1);
}
