/**/ // see golfed version below.


//#define S(v) smoothstep( 1.5, 0., abs(v)/fwidth(v) )
#define S(P) smoothstep( 5./R.y*i, 0., abs( length(P) - 1. ) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 1.2*( u+u - R ) / R.y, V;

    float i=1., v=i;   

    O = vec4( S(U) );                                // circle
    if ( length(U) < 1. )                           // inside only
        for( ; i++ < 9.;  v /= 1.2 )                 // foreach harmonic
            V = U*i, 
            V.x = mod( V.x + i + sign(V.y), 4.) - 2.,// arc center
            O = max(O, S(V)*v ) ;                    // draw arc, grey=v
}




