float circleBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p) - b;
    vec2 d1 = max(d, 0.0);
    float cb = length(d1) * (length(p) - 0.9 * abs(sin(iTime)));
    return cb;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;

    vec3 col  = vec3(0.094, 0.655, 0.667);
    vec3 colPlane = vec3(0.094,0.267,0.667);

    float r = 0.001;
    vec2 pos = vec2(0.0, 0.0);
    vec2 size = vec2(0.5, 0.5);

    float cb = circleBox(uv - pos, size);
    float stepcb = smoothstep(r, r + 0.01, cb);

    col = mix(col, colPlane, stepcb);
    fragColor = vec4(col,1.0);
}
