vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 H = texelFetch( iChannel0, ivec2( fragCoord ), 0 );

    // tanH helps keep it bounded, setting a value -1<>1 to a color pallete to make it look nice!

    vec3 col = pal( tanh(H.y), vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.3,0.20,0.20) );
    fragColor.xyz = col;

}

/* Use this site to find JS event key code
   https://www.toptal.com/developers/keycode
   I arbitrarily used [r]
*/

const int KEY_RESET  = 82;
#define KEY(N) texelFetch( iChannel1, ivec2( N, 0 ), 0 ).x


/* goal is to approximate Uð‘¡ð‘¡ = Î±Uð‘¥ð‘¥ using FDM
   Î± == cÂ² * dtÂ² / dxÂ², here Î± is, arbitrarily, set to .1

   update logic (not at bounds):
   -----------------------------

       uNext = Î±( uLeft - 2*uCenter + uRight ) + 2*uCenter - uCenterPrev


   shader explanation:
   ------------------- 

       â€¢ iChannel0 is stored in Buffer A, everytime it is called, it accesses the current rgba pixel value
       using this and texelFetch we can access neighboring pixels!

       â€¢ uCenterPrev is tricky, fragColor is a 4D vector. We can store the current
       uCenter value in fragColor.y. This vector is saved in buffer A. When the next frame comes,
       accessing fragColor.y will contain the prevoius uCenter.

*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float alpha = 0.1; // Arbitrary! but should be lower than 0.5
    vec2 coordXY = texelFetch(iChannel0, ivec2(fragCoord), 0).xy;

    float uC = coordXY.x; // u Centered 
    float uP = coordXY.y; // u Previous 

    float uL = texelFetch(iChannel0, ivec2(fragCoord + vec2(-1., 0.)), 0).x; // u Left
    float uR = texelFetch(iChannel0, ivec2(fragCoord + vec2(1., 0.)), 0).x; // u Right

    float u = alpha * (uL + uR - 2.*uC) + 2.*uC - uP;

    fragColor = vec4(u,uC,0.0,0.0);

    //fragColor.y = uC;

    if ( KEY(KEY_RESET) > 0.5 || iFrame < 11  ) 
    {

        // reset, set middle of frame to be a vertical gaussian displacement.


        fragColor = texelFetch(iChannel0, ivec2(fragCoord.xy), 0);
        float d = exp(-pow((fragCoord.x-iResolution.x/2.)/10.,2.));
        fragColor = vec4(d,d,d,d);
    };    

}
