#define pi 3.1415926

#define TWEAK1 (iMouse.x/iResolution.x)
#define TWEAK2 (iMouse.y/iResolution.y)
#define TWEAK3 (-iMouse.z/iResolution.x)

vec3 hsv(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.0 * uv - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float theta = atan(uv.y, uv.x);
    //if (theta < 0.0) theta += 2.0*3.1415926;
    theta /= (3.1415926*2.0);

    float stp = 1.0/12.0;
    //theta += 0.5*stp;
    theta = floor(theta/stp)*stp;

    vec3 col = hsv(vec3(theta, 1.0, 1.0));

    col = mix(col, vec3(0), smoothstep(0.9, 0.91, length(uv)));

    col = pow(col, vec3(1.0/2.2));
    fragColor = vec4(col,1.0);
}
