float addFlower(float x, float y, float ax, float ay, float fx, float fy)
{
	float xx    = ( x + sin( 0.2* iTime * fx ) * ax ) * 0.1;
   	float yy    = ( y + cos( 0.2* iTime * fy ) * ay ) * 0.1;
    // iTime : 앞에 곱해서 빠르게 돌려보기

    float angle = atan( yy, xx );
	float zz    = 1.5 * ( cos( 18.0 * angle ) * 0.5 + 0.5 ) 
                  / ( 0.7 * 3.141592 ) + 1.2 * ( sin( 15.0 * angle ) * 0.5 + 0.5 ) 
                  / ( 0.7 * 3.141592);

	return zz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy=(fragCoord.xy/iResolution.x)*2.0-vec2(1.0,iResolution.y/iResolution.x);

   	float x=xy.x;
   	float y=xy.y;

    //  2                 x, y, ax,  ay,   fx,    fy
	float p1 = addFlower(x, y, 3.4, 3.5, 0.0295, 0.0285);
	float p2 = addFlower(x, y, 3.3, 3.5, 0.0242, 0.0271);
	float p3 = addFlower(x, y, 3.2, 3.0, 0.0223, 0.0297);
	float p4 = addFlower(x, y, 3.4, 3.2, 0.0281, 0.0291);
    // ax ay: 꽃의 크기
    // fx fy: 꽃의 움직임 속도

	float p = clamp( ( p1 + p2 + p3 + p4 ) * 0.25, 0.0, 1.0 );
    // p1~4의 합을 [0.0, 1.0] 범위내로 변경

    vec4  PINK = vec4( .49, .385, .38, 1.);
    // 기본 핑크색

	vec4 col;
	if (p < 0.5)
        // 꽃은 가운데 부분
        col = 1.75 * PINK +  
              2.29 * vec4( mix( 0.2, 0.9 , p * 2.5 ), 
                          mix( 0.0, 0.23, p * 1.5 ), 
                          mix( 0.0, 0.23, p * 1.5 ),  
                          1.0 );
	else if (p >= 0.5 && p <= 0.75)
        // 꽃의 끝 부분
        col = 2.4 * PINK +
              0.95 * vec4( mix( 0.6 , 1.0, ( p - 0.5 ) * 5.0 ), 
                           mix( 0.0 , 0.4, ( p - 0.5 ) * 5.0 ), 
                           mix( 0.0 , 0.4, ( p - 0.5 ) * 5.0 ), 
                           1.0);
    else
        // 꽃의 중간 부분
		col = 2.1 * PINK + 
              5.1 * vec4( mix( 0.65 , 0.9 , ( p - 0.75 ) * 5.0 ), 
                          mix( 0.01, 0.16, ( p - 0.75 ) * 3.0 ), 
                          mix( 0.0 , 0.19 , ( p - 0.75 ) * 3.0 ), 
                          1.0 );	


    float s = 15.5;
    // s : 커질수록 테두리가 blur됨

    vec4 O    = vec4( .92 * PINK + 0.63 * floor( col * s ) / s);
    // vec( a * PINK + b * col )
    // a b: 클수록 하얗게 됨
    fragColor = O;

}
