vec2 CenterUV(vec2 fragCoord )//uv的居中与反拉伸https://www.shadertoy.com/view/M3cSDS
{
    return (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
}

float CreatPoint(vec2 uv,float pointYNum,float pointSize)//点阵生成https://www.shadertoy.com/view/XfXczr
{
    return step(pointSize*pointYNum,length(mod(uv *pointYNum + 1.,2.)-1.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = CenterUV(fragCoord);
    float point = CreatPoint(uv,10.,.02);

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col = mix(col,vec3(1.)-col,point);
    fragColor = vec4(col,1.0);
}
