vec2 normalize_coord(vec2 pos){ return (pos*2.-1.)*vec2(iResolution.x/iResolution.y,1.)*.5+.5; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = normalize_coord(fragCoord/iResolution.xy);
    // cartesian distortion
    //uv += vec2(sin(uv.y*12.56+iTime), cos(uv.x*12.56+iTime))*0.05;

    //vec2 offset = vec2(0.5);
    //uv += offset;
    float r = .2;
    vec2 pos = vec2(0.4);
    if (!equal(iMouse.xy,vec2(0)).x) pos = normalize_coord(iMouse.xy/iResolution.xy);
    float d = length(pos-uv)-r;
    vec2 dir = normalize(uv-pos);

    vec3 col = texture(iChannel0, uv-(dir)*d).rgb;
    //col = vec3((-dir)*d,0);
    //if (abs(d - r) < 0.02) col = vec3(1);
    fragColor = vec4(col,1.0);
}
