float heart(vec2 p, float s) {
    p /= s;
    float radius = 0.1;
    vec2 q = p;
    q.x *= 0.5 + .5 * q.y;
    q.y -= abs(p.x) * .63;

      float kr = pow(abs(sin(iTime*2.+ .5)),200.)*radius;
    float kr2 = pow(abs(sin(iTime*2.+ .1)),200.)*radius;
    float r = (length(q) - .5) * s;
    return r-(radius + kr + kr2);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

     float heartDist = heart(uv, 1.0);
    float heartGlow =  smoothstep(0.1, -.1, heartDist) * 1.0;
    vec3 heartCol =  vec3(1.0, .0, .0) * heartGlow ;

    if (heartCol ==  vec3(0.0, .0, .0)){
        fragColor = vec4(vec3(1.0), 1.);
    }else{
        fragColor = vec4(heartCol, 1.);
    }
}
