void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tv = texture(iChannel0, fragCoord / iResolution.xy).rgb;
    float l = dot(tv, vec3(0.3, 0.6, 0.1)) + 0.5;

    l = floor(l * fract(iTime * 0.1) * 13.0 + 0.5) + 34.0;

    fragCoord.x += l;
    fragCoord.y += l * 4.2341;

    float x = fract(fragCoord.x * 0.0052223 + fragCoord.y * 0.13174) * 1342.345;

    float y = fract(fract(x * 0.0342) * 2452.1234 * fract(x * 0.1231) + x * 123.123);

    fragColor = vec4(y, y, y, 1.0);
}
