#define PHI 1.61803398874989484820459

float counter = 0.0f;
const float counter_shift = 0.0006f;

float rand()
{
	counter += counter_shift;
	return fract(sin(counter) * 1000000.0f);
}

float gold_noise(in vec2 xy, in float seed)
{
	return fract(tan(distance(xy*PHI, xy)*seed)*xy.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	const int STARS_SIZE = 700;

	vec2 uv;
	if (iResolution.x > iResolution.y)
	{
		uv = gl_FragCoord.xy * 2.0f / iResolution.x - 1.0f;
		uv.y += (iResolution.x - iResolution.y) / iResolution.x;
	}
	else
	{
		uv = gl_FragCoord.xy * 2.0f / iResolution.y - 1.0f;
		uv.x += (iResolution.y - iResolution.x) / iResolution.y;
	}
	vec2 uv2 = gl_FragCoord.xy / 1000.0f - iResolution.xy / 2000.0f;
	float max_res = max(iResolution.x, iResolution.y);

	vec3 col = mix(vec3(0, 0, 0.1), vec3(0, 0, 0), (uv.x*uv.x + uv.y*uv.y) / 2.0f);
	float noise = gold_noise(uv.xy / 2.0f, 10000000.0f) / 30.0f;
	if(!isnan(noise))
		col += noise;

	vec2 stars[STARS_SIZE];
	for(int i = 0; i < STARS_SIZE; i++)
		stars[i] = vec2(rand()*2.0f-1.0f, rand()*2.0f-1.0f);

	for(int i = 0; i < STARS_SIZE; i++)
	{
		float brightness = (sin(iTime/5.0f + rand()*12.9898f) -0.9f) * 10.0f;
		if (brightness > 0.0f)
		{
			float to_star = sqrt(abs(uv2.x - stars[i].x)) + sqrt(abs(uv2.y - stars[i].y));
			float star_size = rand();
			if (to_star <= 0.1 * star_size)
				col += mix(vec3(0), vec3(1.5, 1.5, 1), brightness);
		}
		else
			counter += counter_shift;
	}

	fragColor = vec4(col,1.0);
}
