void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float angle = radians(-45.0); //angle of stripes
    mat2 rotation = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotatedUV = uv * rotation; // rotate everything by the angle

    //move
    rotatedUV += vec2(iTime * 0.25, iTime * 0.25);

    //stripe position
    float stripePattern = step(0.5, abs(sin(rotatedUV.x * 7.0)));

    vec3 color = vec3(0.0,stripePattern,0.0);

    // Output to screen
    fragColor = vec4(color,1.0);
}
