vec3 drawCircle(vec2 uvTemp,vec2 CirclePos,float radio,vec3 drawCol)
{
    vec3 colTemp = vec3(0,0,0);

    colTemp = distance(uvTemp,CirclePos) < radio ? drawCol : colTemp;

    return colTemp;
}

vec3 drawBox(vec2 uvTemp,vec2 boxPos,float boxX,float boxY,vec3 drawCol)
{
    if(abs(uvTemp.x-boxPos.x)<boxX*0.5&&abs(uvTemp.y-boxPos.y)<boxY*0.5)
        return drawCol;
    return vec3(0,0,0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 Circle1 = drawCircle(uv,vec2(0,0),0.2,vec3(1,0,0));
    vec3 Circle2 = drawCircle(uv,vec2(0.2,0.2),0.2,vec3(0,1,0));
    vec3 Box1 = drawBox(uv,vec2(-0.2,-0.2),0.5,0.5,vec3(0,0,1));
    fragColor = vec4(Circle1+Circle2+Box1,1.0);
}
