#define PI 3.14159
#define TAU 6.28318

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 featuredCloud = vec3(228, 226, 226) / 255.0;
    vec3 background = vec3(91, 158, 191) / 255.0;

    vec3 cloud = vec3(0.0);
    float density = 8.0 / length(uv);
    float noise = sin(dot(uv,uv) * TAU * 4.0 + iTime);
    cloud = mix(featuredCloud, background, density * noise);
    cloud *= (density + length(uv))/(iResolution.y);

    vec3 sky = cloud + background;

    fragColor = vec4(sky,1.0);
}
