void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution = iResolution.xy;

    float slope = -1.0 * (resolution.x / resolution.y);

    float distance = (fragCoord.x - slope * fragCoord.y) + iTime * 150.0;

    float stripeWidth = 150.0; 

    vec3 color = mod(distance, stripeWidth * 2.0) < stripeWidth ? vec3(0.0, 1.0, 0.0) : vec3(0.0);

    fragColor = vec4(color, 1.0);
}
