void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy; // Normalize pixel coordinates
    vec4 color = vec4(0.0, 1.0, 0.0, 1.0); 
    float interval = 400.0;
    float movement = iTime * 200.0; 
    float a = step(mod(fragCoord.x + fragCoord.y + movement, interval) / (interval - 1.0), 0.5);
    // Oscillates
    fragColor = vec4(color.rgb * a, 1.0); // Set the fragment color
}
