void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord = fragCoord - iResolution.xy/2.0;
    float scale = 1.0 + iTime/5.0;
    vec2 uv = fragCoord/scale;
    float col = 0.0;
    if(tan(uv.x*uv.x+uv.y*uv.y) > cos(uv.x*uv.x+uv.y*uv.y))
    {
        col = 1.0;
    }

    fragColor = vec4(0.0, 0.0, col, 1.0);
}
