float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy * 4.0 - 2.0;

    float d = sdCircle(uv - sin(uv.x * iTime * 1.5), 0.75);
    d = d * exp(abs(uv.y) * 10.0 * 0.5 * iTime * 0.5);

    vec3 color = vec3(1.0 - smoothstep(0.0, 0.01, d));
    color = 1. - color;
    float angle = iTime * 1.5;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    uv = rot * uv;

    // Draw the circle
    d = sdCircle(uv - sin(uv * 0.5), 0.5);


    color = mix(color, vec3(1.0), smoothstep(0.0, 0.01, d));




    fragColor = vec4(color.x, 0.,0., 1.0);
}
