float dot2(vec2 v) {
    return dot(v, v);
}

float weight(vec2 pos, vec3 c) {
    //return dot2(pos - c.xy); // center point gradients
    return c.z / (max(dot2(pos - c.xy) - c.z, 0.) + 1e-4);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pos = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y * 2.;
    vec2 m = (iMouse.xy - iResolution.xy * 0.5) / iResolution.y * 2.;

    vec3 c0 = vec3(0.3, 0.6, 0.05);
    vec3 c1 = vec3(0.5, -0.6, 0.1);
    vec3 c2 = vec3(-0.3, 0.6, 0.2);

    c0.xy = m;

    vec3 col0 = vec3(1.000,0.918,0.000);
    vec3 col1 = vec3(0.000,0.800,1.000);
    vec3 col2 = vec3(1.000,0.000,0.518);

    float w0 = weight(pos, c0);
    float w1 = weight(pos, c1);
    float w2 = weight(pos, c2);

    vec3 col = vec3(0.);

    col += col0 * w0;
    col += col1 * w1;
    col += col2 * w2;
    col /= w0 + w1 + w2;

    fragColor = vec4(col, 1.0);
}
