/* This animation is the material of my first youtube tutorial about creative 
   coding, which is a video in which I try to introduce programmers to GLSL 
   and to the wonderful world of shaders, while also trying to share my recent 
   passion for this community.
                                       Video URL: https://youtu.be/f4s1h2YETNY
*/

//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 1.1);
    vec3 b = vec3(0.5, 0.0, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.157);

    return a + b*cos( 6.28318*(c*t+d) );
}
vec2 barrelDistortion(vec2 coord, float amt) {
	vec2 cc = (coord - 0.5);
	float dist = dot(cc, cc);
	return coord + cc * (dist) * amt;
}
mat2 rot(float a) {return mat2(cos(a),sin(a),-sin(a),cos(a));}

//https://www.shadertoy.com/view/mtyGWy
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);
    float time = iTime;
    vec2 p = uv;
 time = time + length(p)*cos(time/4. - 0.04*length(p)*cos(time*time/8. + 0.0002*length(p)*cos(time*time*time/16.)));
	vec3 color = vec3(0.0, 0.3, 0.5);
	    float t=iTime*0.;
	float f = 0.0;
  ;
	float PI = 3.141592;
	for(float i = 0.0; i < 20.0; i++){

		float s = sin(time + i * PI / 1.0) * 1.3;
		float c = cos(time + i * PI / 10.0) * 1.3;

		f += 0.021 / (abs(p.x + c / (0.+length(p))) * abs(p.y + s / (1.+length(p)))) / (1.+length(p));
	}

    for (float i = 0.0; i < 4.0; i++) {
        uv = fract(uv /f* 1.5) - 0.5;
  uv *= rot(.05*t*length(uv));
 float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*.4 + iTime*.4);

        d = sin(d*8. + iTime)/8.;
        d = abs(d);

        d = pow(0.01 / d, 1.2);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
