void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    // Introduce a slow-changing time variable for 10-second modulation
    float timeShift = iTime / 10.0;  // Gradual change over 10 seconds

    for (float i = 1.0; i < 10.0; i++) {
        // Modulate the wave pattern slightly with a time-shifted phase
        uv.x += 0.6 / i * cos(i * 1.5 * uv.y + iTime + timeShift * i);
        uv.y += 0.6 / i * cos(i * 0.5 * uv.x + iTime + timeShift * i);
    }

    // Define the two colors for the gradient
    vec3 colorStart = vec3(0.0, 0.0, 0.0);         // #000000
    vec3 colorEnd = vec3(0.078, 0.094, 0.251);     // #130F40

    // Calculate a gradient factor based on uv coordinates
    float gradientFactor = smoothstep(-1.0, 1.0, uv.y);

    // Blend the two colors based on the gradient factor
    vec3 color = mix(colorStart, colorEnd, gradientFactor);

    fragColor = vec4(color, 1.0);
}
