#define c 1.
#define p 1.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col;

    vec2 VSMin = vec2(-0.5*iResolution.x/iResolution.y, -0.5), VSMax=vec2(0.5*iResolution.x/iResolution.y, 0.5);

    vec2 Points[P];
    vec3 ColMap[P];

    for(int i = 0; i < P; i++){
        Points[i] = vec2(SetRandom(VSMin.x, VSMax.x, c), SetRandom(VSMin.y, VSMax.y, c));
    }
    float Minimum = 1000.;
    for(int i = 0; i < P; i++){
        if(length(uv-Points[i]) < Minimum){
            Minimum = length(uv-Points[i]);
            col = vec3(Minimum);
        }
    }
    for(int i = 0; i < P; i++){
        if(length(uv-Points[i]) < Minimum){
            Minimum = length(uv-Points[i]);
            col = vec3(Minimum);
        }
    }
    for(int i = 0; i < P; i++){
        ColMap[i] = vec3(SetRandom(0., 1., p), SetRandom(0., 1., p), SetRandom(0., 1., p));
    }
    for(int i = 0; i < P; i++){
        if(length(uv-Points[i]) == Minimum){
            col = ColMap[i];
        }
    }

    fragColor = vec4(col,1.0);
}
