float stripePattern(vec2 uv, float frequency, float phase) {
    vec2 dir = normalize(vec2(1.0, 1.0)); // Diagonal direction
    float stripeValue = dot(uv, dir) * frequency + phase;
    return 1.0 - smoothstep(0.1, 0.3, cos(stripeValue)); // Adjusted range for thicker stripes
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Calculate the phase based on time to control the speed of the stripes
    float phase = iTime * 5.0; // Adjust the multiplier to change the speed

    // Calculate the stripe pattern value with the adjusted phase
    float g = stripePattern(uv, 20.0, phase);

    // Create a green color based on the stripe pattern value
    vec3 col = vec3(0.0, g, 0.0);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
