vec2 turn(vec2 uv, float t) {
    vec2 cs = cos(fract(t - vec2(0,0.25)) * 6.2831852);
    return uv * mat2x2(cs.x, -cs.y, cs.y, cs.x);
}

vec4 boring_crap(vec2 uv, int i) {
    float f = float(i);
    vec3 col = 0.5 + 0.5*cos(iTime+f*vec3(5,3,4)+uv.xyx+vec3(0,2,4));
    float alpha = -0.4 - cos(uv.x * 30.0);
    float fw = fwidth(alpha);
    col *= clamp(alpha * 2.0, 0.1, 1.2) - f * 0.03;
    alpha = smoothstep(-fw, fw, alpha);
    return vec4(col, 1) * alpha;
}

vec2 futz(vec2 uv) {

    uv += sin(uv.yx * 7.3 + iTime) * 0.05;
    uv += sin(uv * 3.3 + iTime*0.8) * 0.03;
    uv = turn(uv, 0.157);

    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;

    for (int i = 0; i < 3; ++i) {
        uv = futz(uv);
        uv.yx = futz(uv.yx * 0.777) / 0.777;
    }

    vec4 op = vec4(0);
    for (int i = 0; i < 10; ++i) {
        vec4 colour = boring_crap(uv, i);
        //op = mix(colour, op, op.a);
        op += colour * (1.0 - op.a);
        if (op.a > 0.98) break;
        uv = futz(uv);
        uv = futz(uv.yx + vec2(0,0.2)) - vec2(0,0.2);
        uv = futz(uv.yx * 0.618) / 0.618;
    }

    fragColor = op;
}
