vec2 f_o_rotd(vec2 o_trn, float n_ang_nor){
    float n_tau = radians(360.);
    float n_sin = sin(n_ang_nor*n_tau);
    float n_cos = cos(n_ang_nor*n_tau);
    mat2 o_matr = mat2(
        n_cos, -n_sin, 
        n_sin, n_cos
    );
    return o_trn*o_matr;
}
float fns(float n, float b){
    return sin(n+b)*.5+.5;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_scl_min = min(iResolution.x, iResolution.y);
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/n_scl_min;
    o_trn *= sin(iTime*.2)*4.;
    float n_dcntr = length(o_trn);
    float n_tau = radians(360.);
    float n_circles = 5.;
    float n_ang_nor = fract(atan(o_trn.x, o_trn.y)/n_tau+(1./n_circles/2./2.));
    float n_amp2 = sin(iTime+n_dcntr*n_circles*2.)*(1./(n_circles*1.));
    float n_amp = 1.+sin(n_ang_nor*n_tau*n_circles)*n_amp2;
    float n_dc2 = n_dcntr*n_amp;
    float n = (sin(n_dc2*n_circles*n_tau)*.5+.5);
    //n = pow(n, 1./3.);
    float n2 = sin(n_dc2*n_tau+iTime);
    n2 = 1.-pow(abs(n2), 1./3.)*1.;
    vec3 o = vec3(1.,2.,3.)*n2;
    vec3 odc2 = vec3(
        (sin(n_dc2*n_circles*n_tau+o.r)*.5+.5),
        (sin(n_dc2*n_circles*n_tau+o.g)*.5+.5),
        (sin(n_dc2*n_circles*n_tau+o.b)*.5+.5)
    );
    //odc2.rgb = vec3(n2);


    //odc2 = vec3(sin(n_dcntr1*n_tau*2.+iTime));
    fragColor = vec4(odc2,1.);
}
