#define N 6

float hash(float x) {
    float t = floor(mod(iTime / float(N), float(N)));
    return fract(sin(x * 163.15 + t) * 43768.27) * 2.0 - 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float t = floor(mod(iTime, float(N)));

    vec3 i = vec3(uv.x, uv.y, length(uv));
    int k = 0;
    for(int j = 0; j < N; j++) {
        if(j > int(t)) continue;

        i = tan(vec3(
        dot(vec3(hash(float(k++)), hash(float(k++)), hash(float(k++))), i) + 0.5*hash(float(k++)),
        dot(vec3(hash(float(k++)), hash(float(k++)), hash(float(k++))), i) + 0.5*hash(float(k++)),
        dot(vec3(hash(float(k++)), hash(float(k++)), hash(float(k++))), i) + 0.5*hash(float(k++))) * 3.0);
        i = clamp(i, -5.0, 5.0);
        i = tanh(i);
    }

    fragColor = vec4(i*0.5+0.5,1.0);
}
