void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 20.*fragCoord/iResolution.xy -0.5;
    uv = fract(uv+sin(uv.y+2.*cos(iTime/8.)*uv.y/4.0+2.0)+sin(sin(iTime/6.)*uv.y+2.0))*10.;

    vec3 col =  cos(vec3(0.,uv.x,uv.x+0.4));
    col -= vec3(0.09);

    col = smoothstep(0.1,0.5,col);
    // Output to screen
    fragColor = vec4(col,1.0);
}
