float Circle(vec2 uv, vec2 p, float r, float blur) 
{
    float d = length(uv - p);    

    float c = smoothstep(r, r - blur, d);

    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.);
    vec3 col_yellow = vec3 (1,1,0);
    vec3 col_white = vec3 (1,1,1);


    float face = Circle(uv, vec2(0, 0), .42, .01);
    face -= Circle(uv, vec2(-.2, .15), .1, .01);
    face -= Circle(uv, vec2(.3, .15), .1, .01);

    float mouth = Circle(uv, vec2(.02, -.15), .21, .01);
    mouth -= Circle(uv, vec2(.02, -0.08), .21, .01);

    float pupil = Circle(uv, vec2(-.17, .15), .07, .01);
    pupil += Circle(uv, vec2(.33, .15), .07, .01);

    //basically mix is lerp, using main color > into what color u want > the mask
    col = mix(col.rgb, col_yellow, face - mouth);
    col = mix(col.rgb, col_white, pupil);


    fragColor = vec4(vec3(col), 1);
}
