void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //calcula o aspecto da tela 
    float aspect=iResolution.y/iResolution.x;
    //movimenta o eixo 0 para o centro
    uv=uv-vec2(0.5,0.5);
    //multiplica o y pelo aspecto mantendo o x original
    uv=uv*vec2(1,aspect);

    //somente vetores menores q 0.1
    if(length(uv) < 0.1){
        fragColor = vec4(1,0,0,0);
    }
    else{
        fragColor = vec4(1);
    }



}
