float col,x,y,t,w,h,f;
float a=10.3;
float z=5.;
float k=0.5;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    for (f=0.;f<320.;f++){ //intel HD Graphics can go to 30ish

    a = cos(iTime/10./(2.+sin(iTime/11.)))   ; //neck hurt
    w = iResolution.x;
    h = iResolution.y;
    t = w/2.+(fragCoord.x+fragCoord.y*iResolution.x);
    z = ((iResolution.y/2.-fragCoord.y))/
        ((1300.+iTime*10.)-(f*sin(iTime/10.))*2.)+0.2; //(height)-(tilt)

    x = sin(a     )*(mod(t,w)-w/2.)/z + 
        cos(a     )*(mod(t/w,w)+1000.)/z + iTime*2000. ; //the 1000. is a FOV

    y = cos(a     )*(mod(t,w)-w/2.)/z +
        sin(a+3.14)*(mod(t/w,w)+1000.)/z +450.; 

     col +=float(z<h)*float(z>0.02)*
             mod(sin(sin(sin(sin(sin(sin( //plasmagen
               (x/265.))*2.5+
               (y/130.))+
               (x/130.))+
               (y/601.))/1.3+
               (x/400.))/1.5+
               (y/780.))*(1.+sin(iTime/15.))



                             ,3.)*(z/60.); //the 3. needs to be modified if the f is higher.
     }                                  // the z works here as a fog.     



    // Output to screen
    fragColor = vec4(col,col/2.,col/5.,0.)/2.;
}
