void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Define the size of each block
    float blockSize = 10.0;

    // Calculate the position of the current block
    vec2 blockPos = floor(fragCoord / blockSize) * blockSize;

    // Set the color of the block based on its position
    vec3 blockColor = vec3(0.0);
    if (mod(blockPos.x / blockSize, 2.0) == 0.0) {
        if (mod(blockPos.y / blockSize, 2.0) == 0.0) {
            blockColor = vec3(1.0, 0.0, 0.0); // Red
        } else {
            blockColor = vec3(0.0, 1.0, 0.0); // Green
        }
    } else {
        if (mod(blockPos.y / blockSize, 2.0) == 0.0) {
            blockColor = vec3(0.0, 0.0, 1.0); // Blue
        } else {
            blockColor = vec3(1.0, 1.0, 0.0); // Yellow
        }
    }

    // Output the color of the block
    fragColor = vec4(blockColor, 1.0);
}
