#define T_MAX 50.0
#define SHADOW_T_MAX 10.0

float hash12(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec2 hash22b(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return 2.0 * fract((p3.xx+p3.yz)*p3.zy) - 1.0;
}

mat2 rot2d(float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat2(
        c, -s,
        s, c);
}

float noise(vec2 p)
{
    vec2 ip = floor(p);
    vec2 fp = fract(p);

    float a = dot(hash22b(ip+vec2(0,0)), fp-vec2(0,0));
    float b = dot(hash22b(ip+vec2(1,0)), fp-vec2(1,0));
    float c = dot(hash22b(ip+vec2(0,1)), fp-vec2(0,1));
    float d = dot(hash22b(ip+vec2(1,1)), fp-vec2(1,1));

    vec2 t = smoothstep(vec2(0), vec2(1), fp);
    float res =mix(mix(a, b, t.x),
                   mix(c, d, t.x),
                   t.y);
    return 0.5 + 0.5 * res;
}

float snoise(vec2 p)
{
    return 2.0 * noise(p) - 1.0;
}

float fbm(vec2 p)
{
    float res = 0.0;
    float amp = 0.5;
    float freq = 1.0;
    for (int i = 0; i < 10; ++i)
    {
        res += amp * noise(freq*p);
        //p *= rot2d(0.37);
        amp /= 2.0;
        freq *= 2.0;
    }
    return res;
}

float map(vec3 p)
{
    float ground = p.y - 1.4*fbm(p.xz);
    return 0.75*ground;
}

vec3 calc_normal( in vec3 p ) // for function f(p)
{
    const float h = 0.0001;      // replace by an appropriate value
    #define ZERO (min(iFrame,0)) // non-constant zero
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(p+e*h);
    }
    return normalize(n);
}

float calc_shadow(vec3 p, vec3 l, float k)
{
    float res = 1.0;

    float t = 0.0;
    for (int i = 0; i < 256 && t < SHADOW_T_MAX; ++i)
    {
        float dist = map(p + t * l);
        if (dist < 0.0001)
        {
            return 0.0;
        }
        res = min(res, k*dist/t);
        t += dist;
    }

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.0 * uv - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0.1, 0.8, -1);
    vec3 at = vec3(0, 0.6, 0);
    vec3 cam_z = normalize(at - ro);
    vec3 cam_x = normalize(cross(vec3(0,1,0), cam_z));
    vec3 cam_y = cross(cam_z, cam_x);
    vec3 rd = normalize(uv.x * cam_x + uv.y * cam_y + 1.3 * cam_z);

    float t = 0.0;
    bool hit = false;
    for (int i = 0; i < 256 && t < T_MAX; ++i)
    {
        vec3 p = ro + t * rd;
        float dist = map(p);
        if (abs(dist) < 0.001)
        {
            hit = true;
            break;
        }
        t += dist;
    }

    vec3 sky_col = 0.9*vec3(0.45, 0.6, 0.8);
    vec3 sun_col = 1.1*vec3(0.9, 0.8, 0.7);
    vec3 sun_dir = normalize(vec3(0.4, 0.25, 0.9));

    float sunAmount = max(0.0, dot(rd, sun_dir));

    vec3 col = vec3(0);
    if (hit)
    {
        vec3 p = ro + t * rd;
        vec3 n = calc_normal(p);
        vec3 shadow = pow(vec3(calc_shadow(p+0.003*n, sun_dir, 16.0)), vec3(1.0, 1.25, 1.5));

        vec3 albedo = vec3(0.37, 0.3, 0.23);
        col += max(0.0, dot(n, sun_dir)) * sun_col * albedo * shadow;
        col += 0.05*max(0.0, -n.y) * sun_col * albedo;
        col += 0.1 * max(0.0, dot(n, vec3(0,1,0))) * albedo * sky_col;

        vec3 fog_col = mix(sky_col, sun_col, pow(sunAmount, 8.0));

        //float fogAmount = 1.0-exp(-0.2*t);
        // https://iquilezles.org/articles/fog/
        float a = 0.15;
        float b = 0.3;
        float fogAmount = (a/b) * exp(-ro.y*b) * (1.0 - exp(-t*rd.y*b))/rd.y;
        col = mix(col, fog_col, clamp(fogAmount, 0.0, 1.0));
    }
    else
    {
        col = mix(sky_col, sun_col, pow(sunAmount, 16.0));
    }

    // Output to screen
    col = sqrt(col);
    fragColor = vec4(col,1.0);
}
