#define R(a) mix(q*dot(p,q),p,cos(a))+sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e;vec3 p,q=vec3(-1,2,1);vec2 h;

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    o=vec4(0.);

    for(j=0.;j++<4.;)
        p=1./abs(abs(p=R(iTime))-.02),
        h=vec2(length(p.xz),p.y)-1.6,
        d=min(atan(h.x,h.y),.0)+length(max(h,0.));

    e+=d;

}o+=vec4(sqrt(p*q),1)*5./i;
}
