#define PI 3.14159265359
#define SQ2OV2 0.707106781187

float dot2( in vec2 v ) { return dot(v,v); }

float sdSegment( in vec2 p, in vec2 a, in vec2 b ){
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdTunnel( in vec2 p, in vec2 wh )
{
    p.x = abs(p.x); p.y = -p.y;
    vec2 q = p - wh;

    float d1 = dot2(vec2(max(q.x,0.0),q.y));
    q.x = (p.y>0.0) ? q.x : length(p)-wh.x;
    float d2 = dot2(vec2(q.x,max(q.y,0.0)));
    float d = sqrt( min(d1,d2) );

    return (max(q.x,q.y)<0.0) ? -d : d;
}

float sdHalfCircle( in vec2 p, in float r, in float rb )
{
    p.x = abs(p.x);
    return ((0.>p.y) ? length(vec2(p.x-r, p.y)) : 
                                  abs(length(p)-r)) - rb;
}

float sdVerticalCapsule( vec2 p, float h, float r ){
    p.y -= clamp( p.y, 0.0, h );
    return length( p ) - r; 
}

float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r ){
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

float sdArc( in vec2 p, in vec2 sc, in float ra, float rb )
{
    // sc is the sin/cos of the arc's aperture
    p.x = abs(p.x);
    return ((sc.y*p.x>sc.x*p.y) ? length(p-sc*ra) : 
                                  abs(length(p)-ra)) - rb;
}

//https://www.shadertoy.com/view/l3fcR7
float sdQuarterCircle( in vec2 p, in float r, in float l, in float rb )
{
    return (.0>p.y||.0>p.x)?
        length(p-((p.x>p.y)?vec2(r, -clamp(-p.y, 0., l)):vec2(-clamp(-p.x, 0., l), r)))-rb:
        abs(length(p)-r) - rb;
}

// TODO : 
