//will revisit later to make it fit with the width of all the other characters
float sdA(vec2 p, float thickness){
    p.x = abs(p.x);
    const vec4 long_bar = vec4(0., .4, .175, -.3);
    const float height_of_middle_bar = .33;
    const vec2 middle_bar = mix(long_bar.zw, long_bar.xy, height_of_middle_bar);
    return min(
        sdSegment(p, long_bar.xy, long_bar.zw)-thickness,
        sdSegment(p, middle_bar, vec2(0., middle_bar.y))-thickness);
}

float sdB(vec2 p, float thickness){
    p.y=-abs(p.y-.05);
    vec2 tunnel_size = vec2(.175);
    p.y+=tunnel_size.x;
    p = p.yx;
    return abs(sdTunnel(p, tunnel_size))-thickness;
}

float sdC(vec2 p, float thickness){
    p.y=abs(p.y-.05)-.175;
    return min(
        sdHalfCircle(p, .175, thickness), 
        sdVerticalCapsule(p+vec2(.175, .175), .175, thickness));
}

float sdD(vec2 p, float thickness){
    p.y-=clamp(p.y, .0-.13125, .35-.13125);
    return abs(sdTunnel(p.yx, vec2(.175)))-thickness;
}

float sdE(vec2 p, float thickness){
    p.y-=.05;
    p.y=abs(abs(p.y)-.175);
    return min(
            sdSegment(p, vec2(.175), vec2(-.175, .175)),
            sdSegment(p, vec2(-.175, 0.), vec2(-.175, .175)))-thickness;
}

float sdF(vec2 pos, float thickness){
    pos.y-=.225;
    vec2 p = pos;
    p.y=abs(p.y);
    return min(
            sdSegment(p, vec2(.175), vec2(-.175, .175)),
            sdSegment(pos, -vec2(.175, .525), vec2(-.175, .175)))-thickness;
}

//over 3 square roots because otherwise it looks like shit, might revisit later
float sdG(vec2 pos, float thickness){
    pos.y-=.05;
    vec2 p = pos;
    p.y=abs(p.y)-.175;
    return min(min(min(
        sdHalfCircle(p, .175, thickness), 
        sdVerticalCapsule(p+vec2(.175, .175), .175, thickness)),
        sdVerticalCapsule(pos.yx+vec2(.0875, .0), 0.175, thickness)),
        sdVerticalCapsule(pos+vec2(-.175, .175), .0875, thickness));
}

float sdH(vec2 p, float thickness){
    p.y-=.05;
    p=abs(p);
    return min(
        sdVerticalCapsule(p.yx, .175, thickness),
        sdVerticalCapsule(p-vec2(.175, .0), .35, thickness));
}

float sdI(vec2 p, float thickness){
    p.y-=.05;
    p=abs(p);
    return min(
        sdVerticalCapsule(p.yx-vec2(.35, .0), .175, thickness),
        sdVerticalCapsule(p, .35, thickness));
}

float sdJ(vec2 p, float thickness){
    p.y-=.05;
    return min(min(
        sdHalfCircle(vec2(p.x, -.175-p.y), .175, thickness),
        sdVerticalCapsule(p+vec2(-.175, .175), .525, thickness)),
        sdVerticalCapsule(p.yx-vec2(.35, .0), .175, thickness));
}

float sdK(vec2 p, float thickness){
    p.y-=.05;
    p.y=abs(p.y);
    return min(min(
        sdVerticalCapsule(p.yx+vec2(.0, .175), .175, thickness),
        sdVerticalCapsule(p+vec2(.175, .0), .35, thickness)),
        sdSegment(p, vec2(.0), vec2(.175, .35))-thickness);
}

float sdL(vec2 p, float thickness){
    p.y-=.05;
    return min(
        sdVerticalCapsule(p.yx+vec2(.35, .175), .350, thickness),
        sdVerticalCapsule(p+vec2(.175, .35), .7, thickness));
}

float sdM(vec2 p, float thickness){
    p.y-=.05;
    p.x=abs(p.x);
    return min(
        sdSegment(p, vec2(.0), vec2(.175, .35))-thickness,
        sdVerticalCapsule(p+vec2(-.175, .35), .7, thickness));
}

float sdN(vec2 pos, float thickness){
    pos.y-=.05;
    vec2 p = pos;
    p.x=abs(p.x);
    return min(
        sdSegment(pos, vec2(-.175, .350), vec2(.175, -.35))-thickness,
        sdVerticalCapsule(p+vec2(-.175, .35), .7, thickness));
}

float sdO(vec2 p, float thickness){
    p.y+=.125;
    return abs(sdVerticalCapsule(p, .35, .175))-thickness;
}

float sdP(vec2 p, float thickness){
    p.y-=.05;
    return min(
        abs(sdTunnel(p.yx-vec2(.175, .0), vec2(.175)))-thickness,
        sdVerticalCapsule(p+vec2(.175, .35), .35, thickness));
}

float sdQ(vec2 p, float thickness){
    const vec2 tailpos = vec2(cos(-PI/3.-.2), sin(-PI/3.-.2))*.175;
    return min(
        sdO(p, thickness), 
        sdSegment(p, vec2(.0, -.125)+tailpos, vec2(.175, -.45))-thickness);
}

float sdR(vec2 p, float thickness){

    return min(
        sdP(p, thickness), 
        sdSegment(p, vec2(.0, .05), vec2(.175, -.3))-thickness);
}

float sdS(vec2 pos, float thickness){
    pos.y-=.05;
    vec2 p = pos;
    p.y=abs(p.y);
    pos.y = sign(pos.x)*pos.y;
    pos.y+=.175;
    pos.x = abs(pos.x);
    return min(
        sdHalfCircle(pos, .175, thickness),
        sdHalfCircle(p-vec2(.0, .175), .175, thickness));

}

float sdT(vec2 p, float thickness){
    return min(
        sdVerticalCapsule(p+vec2(.0, .3), .7, thickness),
        sdVerticalCapsule(p.yx+vec2(-.4, .175), .35, thickness));
}

float sdU(vec2 p, float thickness){
    p.x=abs(p.x);
    p.y=-p.y+.05;
    return min(
        sdHalfCircle(p-vec2(.0, .175), .175, thickness),
        sdVerticalCapsule(p+vec2(-.175, .35), .525, thickness));
}

float sdV(vec2 p, float thickness){
    p.x=abs(p.x);
    return sdSegment(p, vec2(.0, -.3), vec2(.175, .4))-thickness;
}

float sdW(vec2 p, float thickness){
    p.x=abs(abs(p.x)-0.0875)+0.0875;
    return sdSegment(p, vec2(.0875, -.3), vec2(.175, .4))-thickness;
}

float sdX(vec2 p, float thickness){
    p.y-=.05;
    p=abs(p);
    return sdSegment(p, vec2(.0), vec2(.175, .35))-thickness;
}

float sdY(vec2 p, float thickness){
    p.x=abs(p.x);
    return min(
        sdSegment(p, vec2(.0), vec2(.175, .4))-thickness,
        sdVerticalCapsule(p+vec2(.0, .3), .3, thickness));
}

float sdZ(vec2 pos, float thickness){
    vec2 p = pos;
    p.y=abs(p.y-.05);
    return min(
        sdVerticalCapsule(p.yx-vec2(.35, -.175), .35, thickness),
        sdSegment(pos, vec2(-.175, -.3), vec2(.175, .4))-thickness);
}

float sda(vec2 p, float thickness, float roundness){
    //p.y-=.05;
    return min(
        sdQuarterCircle(p-vec2(.0, .025), .175, .1, .0),
        abs(sdRoundedBox(p+vec2(.0, .175), 
                         vec2(.175, .125), 
                         vec4(.0, vec3(roundness)))))-thickness;
}

float sdb(vec2 p, float thickness, float roundness){
    return min(
    abs(sdRoundedBox(p+vec2(.0, .05), 
                         vec2(.175, .25), 
                         vec4(roundness)))-thickness,
    sdVerticalCapsule(p+vec2(.175, .3), .7, thickness));
}

//bound if roundness isn't .175 :(
float sdc(vec2 p, float thickness, float roundness){
    p.y=abs(p.y+.05)-.075;
    return (p.x-.175+roundness>.0)?
        sdHalfCircle(p-.175+roundness, roundness, thickness):
        abs(sdRoundedBox(p+vec2(.0, .075), 
                             vec2(.175, .25), 
                             vec4(roundness)))-thickness;
}

float sdd(vec2 p, float thickness, float roundness){
    return min(
    abs(sdRoundedBox(p+vec2(.0, .05), 
                         vec2(.175, .25), 
                         vec4(roundness)))-thickness,
    sdVerticalCapsule(p+vec2(-.175, .3), .7, thickness));
}

//more nightmareish than G holy shit
float sde(vec2 p, float thickness, float roundness){
    return (p.y+.0625>0.)?
        abs(sdTunnel(p-vec2(.0,.025), vec2(.175, .0875)))-thickness:
        min(min(min(
            sdQuarterCircle(vec2(-p.x, -p.y-.125), .175, .0, thickness),
            sdArc(vec2(p.x, -p.y-.125), vec2(SQ2OV2), .175, thickness)),
            sdVerticalCapsule(p.yx+vec2(.0625, .175), .35, thickness)),
            sdVerticalCapsule(p+vec2(.175, .125), .075, thickness));
}

float sdf(vec2 p, float thickness, float roundness){
    return min(min(
        sdVerticalCapsule(p.yx+vec2(-.2, .175), .35, thickness),
        length(p)),
        length(p));
}

vec4 scene(in vec2 p){

    //DO NOT LOOK, VERY BAD
    float offset = .6;
    float thickness = .03 + .02*cos(iTime);

    //float d = sdT(p, thickness);p.x-=offset;d=min(d, sdH(p, thickness));p.x-=offset;d=min(d, sdE(p, thickness));p.x-=offset;p.x-=offset;d=min(d, sdQ(p, thickness));p.x-=offset;d=min(d, sdU(p, thickness));p.x-=offset;d=min(d, sdI(p, thickness));p.x-=offset;d=min(d, sdC(p, thickness));p.x-=offset;d=min(d, sdK(p, thickness));p.x-=offset;p.x-=offset;d=min(d, sdB(p, thickness));p.x-=offset;d=min(d, sdR(p, thickness));p.x-=offset;d=min(d, sdO(p, thickness));p.x-=offset;d=min(d, sdW(p, thickness));p.x-=offset;d=min(d, sdN(p, thickness));p.x-=offset;p.x-=offset;d=min(d, sdF(p, thickness));p.x-=offset;d=min(d, sdO(p, thickness));p.x-=offset;d=min(d, sdX(p, thickness));p.x-=offset;p.x-=offset;d=min(d, sdJ(p, thickness));p.x-=offset;d=min(d, sdU(p, thickness));p.x-=offset;d=min(d, sdM(p, thickness));p.x-=offset;d=min(d, sdP(p, thickness));p.x-=offset;d=min(d, sdS(p, thickness));p.x-=offset;p.x-=offset;d=min(d, sdO(p, thickness));p.x-=offset;d=min(d, sdV(p, thickness));p.x-=offset;d=min(d, sdE(p, thickness));p.x-=offset;d=min(d, sdR(p, thickness));p.x-=offset;p.x-=offset;d=min(d, sdT(p, thickness));p.x-=offset;d=min(d, sdH(p, thickness));p.x-=offset;d=min(d, sdE(p, thickness));p.x-=offset;p.x-=offset;d=min(d, sdL(p, thickness));p.x-=offset;d=min(d, sdA(p, thickness));p.x-=offset;d=min(d, sdZ(p, thickness));p.x-=offset;d=min(d, sdY(p, thickness));p.x-=offset;p.x-=offset;d=min(d, sdD(p, thickness));p.x-=offset;d=min(d, sdO(p, thickness));p.x-=offset;d=min(d, sdG(p, thickness));

    float d = sdf(p, .03, .0);

    //d = sdThreeQuarters(p, .175, thickness);

    float d_baseline = sdSegment(p, vec2(-.5, -.3), vec2(.5, -.3));
    float d_decender_height = sdSegment(p, vec2(-.5, -.45), vec2(.5, -.45));
    float d_x_height = sdSegment(p, vec2(-.5, .2), vec2(.5, .2));
    float d_caps_height = sdSegment(p, vec2(-.5, .4), vec2(.5, .4));
    float d_ascender_height = sdSegment(p, vec2(-.5, .5), vec2(.5, .5));
    float d_char_width = sdSegment(abs(p), vec2(.175, .5), vec2(.175, -.5));

    //coloring taken from iq
    vec3 color = vec3(0.);
    float px = 2./iResolution.y;
    //color = (d>0.0) ? vec3(.9,.6,.3) : vec3(.65,.85,1.);
    //color *= 1. - exp2(-.06/px*abs(d));
    //color *= .8 + .2*cos(iResolution.x*.06*abs(d));
    //color = mix(color, vec3(1.), 1.-smoothstep(-px*12.,px*12.,abs(d)-0.005));
    color = (d>0.0) ? vec3(.9,.6,.3) : vec3(.65,.85,1.);
    color *= 1. - exp2(-.09/px*abs(d));
    color *= .8 + .2*cos(iResolution.x*.6*abs(d));
    color = mix(color, vec3(1.), 1.-smoothstep(-px*1.,px*1.,abs(d)-0.005));


    //debug lines i used to make the characters the same dimentions
    color = mix(color, vec3(1., 0., 0.), 1.-smoothstep(-px,px,abs(d_baseline)-0.005));
    color = mix(color, vec3(1., 0., 0.), 1.-smoothstep(-px,px,abs(d_decender_height)-0.005));
    color = mix(color, vec3(1., 0., 0.), 1.-smoothstep(-px,px,abs(d_x_height)-0.005));
    color = mix(color, vec3(1., 0., 0.), 1.-smoothstep(-px,px,abs(d_caps_height)-0.005));
    color = mix(color, vec3(1., 0., 0.), 1.-smoothstep(-px,px,abs(d_ascender_height)-0.005));
    color = mix(color, vec3(1., 0., 0.), 1.-smoothstep(-px,px,abs(d_char_width)-0.005));

    return vec4(color, d);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 m = iMouse.xy/iResolution.xy;
    vec2 p = uv-.5;
    m-=.5;
    p.x = p.x*iResolution.x/iResolution.y;
    m.x = m.x*iResolution.x/iResolution.y;
    //p.x+=.81;
    //p*=15.5;
    //m.x+=.81;
    //m*=15.5;
    vec3 col = scene(p).xyz;

    float px = 2./iResolution.y;

    float d = scene(m).w;
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, px, abs(length(p-m)-abs(d))-0.0025));



    // Output to screen
    fragColor = vec4(col,1.0);
    //fragColor = vec4(p, 0., 1.);
}
