void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float effect = (iMouse.x/iResolution.x)/10.;
    vec2 uv = fragCoord/iResolution.xy;

    fragColor.r = texture(iChannel0,vec2(uv.x-effect, uv.y)).r;
    fragColor.g = texture(iChannel0,uv).g;
    fragColor.b = texture(iChannel0,vec2(uv.x+effect, uv.y)).b;
    fragColor.a = 1.;


}
