void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // [0, 0] at bottom left, [1, 1] at top right 
    vec2 uv = fragCoord / iResolution.xy;

    // [0, 0] at centre, [-1, -1] at bottom left, [1, 1] at top right
    vec2 n = uv * 2.0 - 1.0;

    // Apply aspect ratio (otherwise our circle gets distorted)
    n.x *= iResolution.x / iResolution.y;

    // Black-red-green-yellow
    vec3 a = vec3(uv.x, uv.y, 0.0);

    // Blue-magenta-teal-white gradient
    vec3 b = vec3(uv.x, uv.y, 1.0);

    // Distance from centre to current fragment (0 at centre, 1 at edges)
    float d = length(n);

    // Black background
    vec3 background = vec3(0.0, 0.0, 0.0);

    // Blend between gradients via nsin
    vec3 gradient = mix(a, b, sin(iTime) * 0.5 + 0.5);

    // Blend between background & gradient based on distance from centre
    vec3 color = mix(gradient, background, d);

    fragColor = vec4(color, 1.0);
}
