float sdfBox(in vec2 p, in vec2 b){
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void split(inout vec2 uv, in float times){
    uv = fract(uv * times) - 0.5;
}

float sWave(float amp, float freq, float time){
    return amp * (time*freq - floor(time*freq));
}

vec3 mixColor(float t){
    vec3 a = vec3(-3.142, 1.000, 1.000);
    vec3 b = vec3(0.500, 0.500, 0.500);
    vec3 c = vec3(1.000, 2.000, 1.000);
    vec3 d = vec3(-3.075, 0.000, 0.000);

    return a + b*cos(6.28318*(c*t+d));
}

void rot2d(inout vec2 uv, in float ang){
    uv = vec2(uv.x*cos(ang) - uv.y*sin(ang),
    uv.y*cos(ang) + uv.x*sin(ang));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    float scale = sWave(1.0, 1.0, -iTime * .2) - 0.5;

    uv = uv * sin(scale * 3.1415926535);


    vec2 _uv_ = uv;
    float ang = radians(45.);
    float gd = abs(sdfBox(uv, vec2(1., 1.)));

    //rot2d(uv, ang);

    vec3 finalCol = vec3(0.0);

    for(float i=0.; i<4.; i++){
        split(uv, 1.);
        rot2d(uv, ang);
        float d = abs(sdfBox(uv, vec2(2., 2.)));
        float rd = d * exp(-gd);


        d = sin(d*16. + iTime)/16.;
        d = abs(d);
        d = 0.002/d;

        //if you dislike bloom, enable this
        //d = smoothstep(0.0, 1.0, d);

        vec3 col = mixColor(rd + iTime*.4 + i*.2);
        col *= d;

        finalCol += col;
    }

    fragColor = vec4(finalCol, length(finalCol)/sqrt(3.0));
}
