//#define draw(q) o *= smoothstep(0.0, 0.01, abs(q))
#define draw(q) o *= smoothstep(0.0, length(fwidth(I)), abs(q)) /* is fwidth supported on all devices? */

float offset_semi_circle(float x)
{
    return sqrt(1.0 - (x-1.0)*(x-1.0));
}

float offset_semi_circle_derivative(float x)
{
    return -((x-1.0))/sqrt(-(x-2.0)*x);
}

float draw_offset_semi_circle(vec2 p)
{
    return (offset_semi_circle(p.x)-p.y)/length(vec2(offset_semi_circle_derivative(p.x),-1.0)); // divide by length of the gradient to get a constant width line
}

float hyperboloid(float a, float b, float s, float x)
{
    return sqrt(abs(a*a+b*b*x*x))/s;
}

float hyperboloid_dervative(float a, float b, float s, float x)
{
    return (b*b*x)/sqrt(a*a + b*b*x*x)/s;
}

float draw_hyperboloid(float a, float b, float s, vec2 p)
{
    return (hyperboloid(a, b, s, p.x)-p.y)/length(vec2(hyperboloid_dervative(a, b, s, p.x),-1.0)); // divide by length of the gradient to get a constant width line
}

#define pi 3.1415926
#define tau (pi+pi)

float get_s(float a, float b) // https://www.shadertoy.com/view/XfjcRy hyperbola circle connector (jt)
{
    // thanks to wolframalpha!
    return (sqrt(2.0)/4.0*a*a*sqrt(-((sqrt(a*a*a*a + 4.0*a*a*b*b) - a*a)*((0.5*(sqrt(a*a*a*a + 4.0*a*a*b*b) - a*a))/(b*b) - 2.0))/(b*b)))/sqrt(a*a + (0.25*pow(sqrt(a*a*a*a + 4.0*a*a*b*b) - a*a,2.0))/(b*b)) + (sqrt(2.0)/4.0*sqrt(a*a*a*a + 4.0*a*a*b*b)*sqrt(-((sqrt(a*a*a*a + 4.0*a*a*b*b) - a*a)*((0.5*(sqrt(a*a*a*a + 4.0*a*a*b*b) - a*a))/(b*b) - 2.0))/(b*b)))/sqrt((a*a) + (0.25*pow(sqrt(a*a*a*a + 4.0*a*a*b*b) - a*a,2.0))/(b*b));
}

vec2 get_c(float a, float b) // https://www.shadertoy.com/view/XfjcRy hyperbola circle connector (t)
{
    float x = (0.5*(sqrt(a*a*a*a + 4.0*a*a*b*b) - a*a))/(b*b);
    return vec2(x, sqrt(1.0-pow(x-1.0, 2.0)));
}

void mainImage(out vec4 o, in vec2 I) // [-1,+1]
{
    vec2 R = iResolution.xy;
    I = 1.5*(I+I-R)/R.y;

    I.y += 1.5;

    o = vec4(1);

    {
        float a = mix(0.2, 1.0, 0.5+0.5*cos(iTime*tau/15.0));
        float b = mix(0.2, 2.0, 0.5+0.5*sin(iTime*tau/15.0));
        float s = get_s(a,b);
        vec2 c = get_c(a,b);

        {
            draw(draw_hyperboloid(a, b, s, I));
            //if(I.x > 0.0 && I.x < 2.0) draw(draw_offset_semi_circle(I)); // using semi circle function
            draw(length(I-vec2(1,0))-1.0); // using regular circle
        }

        o = 0.5 + 0.5 * o;

        {
            if(I.x < c.x) draw(draw_hyperboloid(a, b, s, I));
            //else if(I.x < 2.0) draw(draw_offset_semi_circle(I)); // using semi circle function
            else draw(length(I-vec2(1,0))-1.0); // using regular circle

            draw(length(I - c) - 0.015);
        }
    }

    o = sqrt(o); // approx gamma
}
