#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U) 
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
float map (vec3 p) {
    mat2 m = ei(-.7);
    p.y += .25;
    for (float i = 0.; i < 20.; i++) {

        p = abs(p);
        p-=.25*exp2(-.8*i);
        p.xz *= m;
        p.yz *= m;
        p.xy *= m;


    }

    return length(p)-.0001;
}
vec3 normal (vec3 p) {
    vec2 e = vec2(1e-4,0);
    return normalize(vec3(
        map(p+e.xyy)-map(p-e.xyy),
        map(p+e.yxy)-map(p-e.yxy),
        map(p+e.yyx)-map(p-e.yyx)
    ));
}
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

#define pi 3.14159265359

Main {
    if (iFrame >0) {Q = A(U);return;}
    vec3 p = vec3(0,0,-1);
    vec3 d = normalize(vec3(2.*(U-.5*R)/R.y,1));
    Q = vec4(0);

    for (float i = 0.;i < 100.; i++) {
        float m = map(p);
        p += d*m;

        if (m < 1e-4) {Q.xyz = p;break;}
        if (m > 10.) break;
    }

}

Main {
    Q = B(U);
    vec3 p = A(U).xyz;
    vec3 light = vec3(4,0,-1);
    vec4 h = hash44(vec4(U,iTime,iFrame));
    float a = h.x*2.*pi;
    float b = h.y*pi;
    vec3 d = vec3(vec2(cos(a),sin(a))*sin(b),cos(b));
    for (float i = 0.; i < 50.; i++){
        float n = map(p);
        float m = min(max(n,.001),length(p-light)-2.);

        p += d*m;
        vec4 h = hash44(vec4(p*1000.,i));
        vec3 dd = (h.xyz*2.-1.)*pow(h.w,10.);
        if (m < 0.02) d = normalize(d+.5*dd);
        if (m > 10.) break;

    }

    Q.xyz += 100.*(exp(-4.*max(length(p-light)-2.,0.)))*max(sin(2.+3.*d.y+vec3(1,2,3)),0.);

}
