// Fork of "Advection Art" by wyatt. https://shadertoy.com/view/lfBczd
// 2024-08-05 12:28:06

Main {
    Q = B(U)/float(iFrame);

}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U) 
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

#define pi 3.14159265359

Main {
    if (iFrame > 1) {Q = A(U);return;}
   float k = 1.;
   U -= .5*R;
   U *= ei(.5);
   U += .5*R;
   for (float i = 0.; i < 100.; i++) {
       k = .4;
       U -= .1*vec2(cos(k*U.y),sin(k*U.x));
       k = .2;
       U -= .2*vec2(cos(k*U.y),sin(k*U.x));
       k = .1;
       U -= .5*vec2(cos(k*U.y),sin(k*U.x));
       k = .05;
       U -= .8*vec2(cos(k*U.y),sin(k*U.x));
       k = .025;
       U -= .7*vec2(cos(k*U.y),sin(k*U.x));
       k = .0125;
       U -= 1.5*vec2(cos(k*U.y),sin(k*U.x));
       k = .5*.0125;
       U -= 1.*vec2(cos(k*U.y),sin(k*U.x));
       k = .25*.0125;
       U -= .5*vec2(cos(k*U.y),sin(k*U.x));
   }
   Q = vec4(1)*(.5+.5*sin(8.*length(U-.5*R)/R.y));

}

Main {
    Q = B(U);
    vec4 h = hash44(vec4(U,iTime,iFrame));
    U += h.zw*2.-1.;
    float a = h.x*2.*pi;
    float b = h.y*pi;
    vec3 d = vec3(vec2(cos(a),sin(a))*sin(b),cos(b));
    vec4 q = A(U);
    vec3 p = vec3(U,100.*q.x);
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    vec3 no = normalize(vec3(e.x-w.x,n.x-s.x,1./100.));
    d.z = abs(d.z);
    d = normalize(no+5.*d);
    p += d*.5;
    vec3 light = vec3(.5*R,2.*R.y);
    for (float i = 0.; i < 100.; i++) {
        vec4 q = A(p.xy);
        float m = min(p.z-100.*q.x,length(p-light)-100.);
        p += .8*d*m;
    }
    Q += 50.*exp(-.1*(length(p-light)-100.))*max(sin(.05*(p.x-light.x)+vec4(1,2,3,4)),0.);

}
