// https://www.facebook.com/groups/mcescher/posts/10160241063682197
// https://www.facebook.com/groups/tiling/posts/1880790585677811

#define C(P,R)  (P).x > 0. && (P).y > 0. ? abs(length((P)/R)-1.)*R.x : 1e9 // ellipse quarter

void mainImage( out vec4 O, vec2 u )
{
    float l = 9., d, 
          s = exp2(mod(iTime,2.));                           // scaling. cycles at t=2 ( and sym at t>1 )
    vec2  R = iResolution.xy,
          U = ( .5*R - u ) / R.y / s;                        // normalized coordinates 
    U.x = abs(U.x);                                          // horizontal symmetry

    int i;
    for( ; i < 13; i++, U *= 2., U.y = -U.y ) {              // fractal iterations
        d = min( C( (U-vec2(.8,.5))*vec2(-1,1), vec2(.5) ),  // top "torus" part
                 C(  U-vec2(.8,0 )         , vec2(.8,1)) );
        U.y > -1. && U.y <=0. ? d = abs(U.x-1.1-.5*cos(U.y*3.14)) : d; // saddle part
        l = min(l*2.,d);                                     // union of all curves
    }
    O = sqrt( l * s*R.yyyy / float(1<<i) );                  // antialias draw
}
