// variation of 
// https://www.shadertoy.com/view/4csBzX
#define t iTime

void mainImage(out vec4 o, vec2 u) {
    vec2   r = iResolution.xy, q,
           p = vec2(u + u - r) / r.y,

           polar0 = vec2(
                        atan(p.y, p.x) ,
                        length(p) / dot(p, p) - 1.
                    ) * 12. / 3.14,

           polar1 = vec2(
                        atan(p.y, p.x) ,
                        log(length(p))
                    ) * 12. / 3.14,

           polar2 = p * 18. / dot(p, p);

    float T = mod(t * .12, 5.),
          a = smoothstep(1., 1.2, T), 
          b = smoothstep(2., 2.3, T), 
          c = smoothstep(3., 3.3, T), 
          d = smoothstep(4., 4.2, T); 

    p = mix(8. * p, 
            mix(polar1.yx, 
                mix(polar2, 
                    mix(polar1, 
                        polar0, 
        d), c), b), a);

    vec2  k = floor(p * 2.);
    float t = 12. * fract(sin(dot(k.x, k.y) * 453.2734) * 994.3434) + t * .75;

    o *= 0.;
    o = mix(o, vec4(.4, 0, 0, 0), smoothstep(-1., -1.+.1, cos(t)));
    o = mix(o, vec4(0, .4, 0, 0), smoothstep(-.34, -.34+.1, cos(t)));
    o = mix(o, vec4(0, 0, .4, 0), smoothstep(.33, .33+.1, cos(t)));

}
