void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 keyColor = vec3(0.0, 1.0, 0.0);
    float threshold = 0.6;
    float smoothness = 0.15;

    vec4 channel0 = texture(iChannel0, uv);
    vec4 channel1 = texture(iChannel1, uv);

    float dist = distance(keyColor, channel1.rgb);

    float alpha = smoothstep(threshold, threshold + smoothness, dist);

    fragColor = mix(channel0, channel1, alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / max(iResolution.x, iResolution.y);

    uv = abs(uv);
    uv = fract(uv);
    float pattern =  sin(iTime + 60.0 * (uv.x + uv.y));
    pattern = smoothstep(0.1, 0.8, pattern);

    vec3 col = vec3(pattern) - cos(iTime + vec3(2.0, 4.0, 0.0) * vec3(uv.xy, 0.0));

    fragColor = vec4(col, 1.0);
}
